import React,{useState} from 'react';
import './App.css';
import Person from './Person/Person.js'; //This "Person" name is of my own choice
//But the first letter should be in capital
//As HTML recognizes small tags as its own defult tags

function App() {
   const [persons,setPerson]=useState([
     {name:'abdul',age:12},
     {name:'shakib', age:69},
     {name:'sami',age:11}

  ])


const nameChangeHandler=(event) => {
  setPerson([
    {name:'samad',age:12},
    {name:event.target.value, age:69},
    {name:'javed',age:11},
  ])

} 

const switchNameHandler=(newName)=>{
  //console.log('Was Clicked');
  //persons[0].name='Abdul';
    setPerson([
    {name:newName,age:12},
    {name:'shahida', age:69},
    {name:'javed',age:11}

   ])
}

  return (
     <div className="App">
       <h1> Hi I'm a  React App </h1>
       <p> This is working! </p>
       <button onClick={() =>  switchNameHandler()}>SwitchName</button>
       <Person 
       name = {persons[0].name} 
       age ={persons[0].age}/>
        <Person 
        name ={persons[1].name} 
        age ={persons[1].age}
        click={switchNameHandler.bind(this,'Kashan')}
        changed={nameChangeHandler}>And Hobby is Tennis

        </Person>
       <Person 
       name={persons[2].name} 
       age ={persons[2].age} />  

     </div>
   );

  //return React.createElement('div',{className:'App'},React.createElement('h1',null,'Samad'))
}

export default App;
