//As HTML recognizes small tags as its own defult tags

function App() {
   const [persons,setPerson]=useState([
     {name:'samad',age:12},
     {name:'shakib', age:69},
     {name:'sami',age:11}

  ])


  return (
     <div className="App">
       <h1> Hi I'm a React App </h1>
       <p> This is working! </p>
       <button>SwitchName</button>
       <Person name = {this.state.persons[0].name} age ={this.state.persons[0].age}/>
        <Person name ={this.state.persons[1].name} age ={this.state.persons[1].age}>And Hobby is Tennis</Person>
       <Person name={this.state.persons[2].name} age ={this.state.persons[2].age} />  

     </div>
   );

  //return React.createElement('div',{className:'App'},React.createElement('h1',null,'Samad'))
}

export default App;
