import React, {Component} from 'react';
import './App.css';
import Person from './Person/Person' ;

class App extends Component {

    state = {
    persons : [
        {name: 'Abdul', age :20},
        {name: 'Shakib', age :26},
        {name: 'Sami', age :30}

        ]
    }

    switchNameHandler = (newName) => {
        //console.log('Was Clicked');
        //this.state.persons[0].name="Abdul"; DON'T USE THIS SYNTAX REACT DOESN'T RECOGNIZES IT
        this.setState({ persons : [
            {name: newName, age :20},
            {name: 'Shahida', age :26},
            {name: 'Javed', age :30}

        ]})
    }

    nameChangedHandler = (event) => {
        this.setState({ persons : [
            {name: 'Abdul', age :20},
            {name: event.target.value , age :26},
            {name: 'Javed', age :30}

        ]})
    }

    render() {
        return (
            <div className="App" >
                <h1> Hi I'm a React App</h1> 
                <p>This is working ! </p>
                <button onClick={ () => this.switchNameHandler('Khawaja') }>Switch Name</button>
                <Person 
                 name = {this.state.persons[0].name} 
                age = {this.state.persons[0].age} />
                <Person 
                 name ={this.state.persons[1].name} 
                 age = {this.state.persons[1].age}
                 click ={this.switchNameHandler.bind(this,'Max!@')} >and Hobby is Tennis</Person> 
                <Person
                 name = {this.state.persons[2].name} 
                 age = {this.state.persons[2].age} /> 
            </div>
        );
    }
}

export default App
