import React, {Component} from 'react';
import './App.css';
import Person from './Person/Person' ;

class App extends Component {

    state = {
    persons : [
        {name: 'Abdul', age :20},
        {name: 'Shakib', age :26},
        {name: 'Sami', age :30}

        ]
    }

    switchNameHandler = (newName) => {
        //console.log('Was Clicked');
        //this.state.persons[0].name="Abdul"; DON'T USE THIS SYNTAX REACT DOESN'T RECOGNIZES IT
        this.setState({ persons : [
            {name: newName, age :20},
            {name: 'Shahida', age :26},
            {name: 'Javed', age :30}

        ]})
    }

    nameChangedHandler = (event) => {
        this.setState({ persons : [
            {name: 'Abdul', age :20},
            {name: event.target.value , age :26},
            {name: 'Javed', age :30}

        ]})
    }

    render() {
        return (
            <div className="App" >
                <h1> Donate Chairty </h1> 


                 <button onClick={ () => this.switchNameHandler('Khawaja') }>Switch Name</button>
            </div>
        );
    }
}

export default App
